Map = {}; // Map action types to their reducers\n\n    // Builder pattern implementation\n    const builder = {\n      addCase: (actionCreator, reducer) => {\n        // Store the reducer function keyed by action type\n        extraReducerMap[actionCreator.type] = reducer;\n      },\n    };\n\n    // Call the extraReducers function with our builder\n    extraReducers(builder);\n\n    // Step 4: Merge extra reducers with main reducer\n    const originalReducer = reducer;\n    reducer = (state, action) => {\n      // First check if this action should be handled by extraReducers\n      const extraReducer = extraReducerMap[action.type];\n      if (extraReducer) {\n        // Use Immer to handle the extra reducer\n        return produce(state, (draft) => extraReducer(draft, action));\n      }\n      // Fall back to original reducer for slice-specific actions\n      return originalReducer(state, action);\n    };\n  }\n\n  // Return the complete slice object\n  return {\n    actions: actionCreators, // All generated action creators\n    reducer, // The combined reducer function\n    actionTypes, // Action type constants (for debugging)\n  };\n}\n'})}),"\n",(0,r.jsx)(t.h3,{id:"immer-integration",children:"Immer Integration"}),"\n",(0,r.jsx)(t.p,{children:'Immer is the secret sauce that makes Redux Toolkit\'s "mutative" syntax work. It creates a draft copy of your state that you can safely mutate, then produces an immutable result. This eliminates the verbose spread syntax required in traditional Redux while maintaining immutability.'}),"\n",(0,r.jsx)(t.p,{children:(0,r.jsx)(t.strong,{children:"What this code demonstrates:"})}),"\n",(0,r.jsxs)(t.ul,{children:["\n",(0,r.jsxs)(t.li,{children:[(0,r.jsx)(t.strong,{children:"Input"}),': State object and "mutative" operations']}),"\n",(0,r.jsxs)(t.li,{children:[(0,r.jsx)(t.strong,{children:"Process"}),": Immer creates a draft proxy, tracks changes, and produces immutable result"]}),"\n",(0,r.jsxs)(t.li,{children:[(0,r.jsx)(t.stron